var state = context.get("STATE")||0;

//  Look for a !X message to get values.
if (msg.payload != "X")     //Do this if the message is NOT "X"
{
    //
    //  Background colours first.
    //
    context.set("ABGC", msg.colourA);
    context.set("BBGC", msg.colourB);
    //
    //  Now do text.
    //
    context.set("Atxt", msg.txtA);
    context.set("Btxt", msg.txtB);
    //
    //  Font colours.
    //
    context.set("AFC",msg.txtclrA);
    context.set("BFC",msg.txtclrB);
    //
    //  Payloads.
    //
    context.set("PayloadA", msg.payloadA);
    context.set("PayloadB", msg.payloadB);
    //
    //  Topic.
    //
    if (msg.topicSET !== null)
    {
        context.set("Topic",msg.topicSET);
    } else
    {
        context.set("Topic","~");
    }
    return;
}
//      Now on to the real stuff.
if (msg.payload == "X")
{
    state = (state + 1)% 2;
    //node.warn(state);
    context.set("STATE",state);
}
if (state === 0)
{
    //  Condition A
    msg.payload = context.get("PayloadA");
    msg.colour = context.get("ABGC");
    msg.txt = context.get("Atxt");
    msg.fontclr = context.get("AFC");
} else
{
    //  Condition B
    msg.payload = context.get("PayloadB");
    msg.colour = context.get("BBGC");
    msg.txt = context.get("Btxt");
    msg.fontclr = context.get("BFC");
}
if (context.get("Topic") == "~")
{
    msg.topic = "";
} else
{
    msg.topic = context.get("Topic");
}
return msg;

